use std::sync::{RwLock, Arc};

use artewald_engine_lib::{voxel::{Chunk, Material, VoxelData}, threadpool::ThreadPoolHelper};
use nalgebra::{Vector4, Vector3, Vector2};

use self::scene_saver::{save_voxel_data_to_file, does_scene_exist, load_voxel_data_from_file};

mod scene_saver;

const SIMPLE_SCENE_NAME: &str = "simple";
const COMPLEX_SCENE_NAME: &str = "complex";
const MIRROR_SCENE_NAME: &str = "mirror";

fn get_voxels(chunk: &mut Chunk) -> (Vec<VoxelData>, Vec<Material>) {
	chunk.get_oct_tree(
        Vector3::new(0.0, 0.0, 0.0),
        (90.0_f32 / 1080.0_f32).to_radians(),
	)
}

pub fn create_simple_scene(chunk: &mut Chunk, thread_pool: Arc<RwLock<ThreadPoolHelper>>) -> (Vec<VoxelData>, Vec<Material>) {

	if does_scene_exist(SIMPLE_SCENE_NAME) {
		let (voxels, materials) = load_voxel_data_from_file(SIMPLE_SCENE_NAME);
		return (voxels, materials);
	}

	let mut green_material = Material::new_default();
    green_material.color = Vector4::new(1.0, 1.0, 1.0, 1.0);
    green_material.smoothness = 1.0;
    green_material.specular_probability = 1.0;//0.15;
    let mut red_material = Material::new_default();
    red_material.color = Vector4::new(1.0, 0.0, 0.0, 1.0);
    let mut light_material = Material::new_default();
    light_material.emissive_color = Vector3::new(1.0, 1.0, 1.0);
    light_material.emissive_strength = 1.0;

    chunk.fill_voxels(
        thread_pool.clone(),
        Vector3::new(Vector2::new(5, 10), Vector2::new(0, 5), Vector2::new(3, 15)),
        red_material,
    );
    chunk.fill_voxels(
        thread_pool.clone(),
        Vector3::new(Vector2::new(0, 3), Vector2::new(2, 5), Vector2::new(5, 10)),
        green_material,
    );
    chunk.fill_voxels(
        thread_pool.clone(),
        Vector3::new(
            Vector2::new(0, 15),
            Vector2::new(10, 11),
            Vector2::new(0, 20),
        ),
        light_material,
    );

	let (voxels, materials) = get_voxels(chunk);

	save_voxel_data_to_file(voxels.clone(), materials.clone(), SIMPLE_SCENE_NAME);
	(voxels, materials)
}

